import express from 'express'
import { ArticleController } from './../../controllers/articleController.js'
import multer from 'multer';
import { fileFilter } from '../../helpers/fileFilter.js';
import { authenticate } from '../../middlewares/authenticate.js';
import { articleValidation } from '../../validations/articleValidation/article.validation.js';

const route = express.Router()
const storage = multer.diskStorage({});

const uploads = multer({ storage, fileFilter });
const articleControllers = new ArticleController()
route.post('/', authenticate, uploads.single('image'), articleValidation, articleControllers.createArticle)
route.get('/', articleControllers.getAllArticles)
route.get('/:id', articleControllers.getArticle)
route.put('/:id', authenticate, uploads.single('image'), articleControllers.updateArticle)
route.delete('/:id', authenticate, articleControllers.deleteArticle)

export default route

import { createArticleService, deleteArticle, getAllArticlesService, getOneArticleService, updateArticle } from "../services/articleServices.js"
import { uploadFile } from "../helpers/fileUpload.js"
export class ArticleController {

    async createArticle(req, res, next) {
        try {
            req.body.image = await uploadFile(req)
            const data = {
                title: req.body.title,
                content: req.body.content,
                image: req.body.image,
                create_at: new Date()
            }
            const article = await createArticleService(data)
            res.status(200).json({ status: 200, message: "Article created successfully", data: article })
        } catch (error) {
            console.log(error)
        }
    }

    async getAllArticles(req, res, next) {
        try {
            const articles = await getAllArticlesService()
            res.status(200).json({ status: 200, message: "These are all the articles", data: articles })
        } catch (error) {
            console.log(error)
        }
    }

    async getArticle(req, res, next) {
        try {
            const article = await getOneArticleService(req.params.id)
            if (typeof article !== "string") {
                res.status(200).json({ status: 200, message: "article retieved successfully", data: article })
            } else {
                res.status(404).json({ status: 404, message: article })
            }

        } catch (error) {
            console.log(error)
        }
    }

    async updateArticle(req, res, next) {
        try {
            if (req.file) {
                req.body.image = await uploadFile(req)
            }
            const article = await updateArticle(req.params.id, req.body)
            res.status(200).json({ status: 200, message: "article updated successfully", data: article })
        } catch (error) {
            console.log(error)
        }
    }

    async deleteArticle(req, res, next) {
        try {
            const result = await deleteArticle(req.params.id)
            res.status(200).json({ status: 200, message: result })
        } catch (error) {
            console.log(error)
        }
    }
}

import Article from "../models/article.js";

export const createArticleService = async (data) => {
    const article = await Article(data)
    article.save()
    return article
}

export const getAllArticlesService = async () => {
    const articles = await Article.find()
    return articles
}

export const getOneArticleService = async (id) => {
    try {
        const article = await Article.findOne({ _id: id })
        if (!article) {
            return "article article not found"
        } else {
            return article
        }
    } catch (error) {
        return "article article not found"
    }

}

export const updateArticle = async (id, data) => {
    const article = await Article.findOne({ _id: id });
    if (!article) {
        return `Article with id: ${id} doesn't exist`
    } else {
        article.title = data.title ? data.title : article.title
        article.content = data.content ? data.content : article.content
        article.image = data.image ? data.image : article.image
        const updatedArticle = await article.save()
        return updatedArticle
    }
}

export const deleteArticle = async (id) => {
    try {
        const result = await Article.findByIdAndDelete(id)
        if (!result) {
            return "the article you are trying to delete does not exist"
        } else {
            return "Article deleted successfully"
        }
    } catch (error) {
        return "the article you are trying to delete does not exist"
    }

}

